import sysfrom PyQt5.QtWidgets import QApplication, QWidget, QMainWindowfrom PyQt5.QtGui import QPainter, QColor, QBrushfrom random import randintfrom ui_file import Ui_MainWindowfrom PyQt5 import QtCore, QtGui, QtWidgetsclass Example(QMainWindow, Ui_MainWindow):    def __init__(self):        super().__init__()        self.setupUi(self)        self.pushButton.clicked.connect(self.onClicked)        self.flag = False    def onClicked(self):        self.flag = True        self.update()    def paintEvent(self, e):        if self.flag:            qp = QPainter()            qp.begin(self)            self.drawRectangles(qp)            qp.end()    def drawRectangles(self, qp):        col = QColor(0, 0, 0)        col.setNamedColor('yellow')        qp.setPen(col)        qp.setBrush(col)        for i in range(randint(1, 100)):            w = randint(1, 50)            qp.drawEllipse(randint(0, 200), randint(0, 50), w, w)if __name__ == '__main__':    app = QApplication(sys.argv)    ex = Example()    ex.show()    sys.exit(app.exec_())